/* Drop Tables */

DROP TABLE IF EXISTS rep;
DROP TABLE IF EXISTS review_brd;
DROP TABLE IF EXISTS zzim;
DROP TABLE IF EXISTS class;
DROP TABLE IF EXISTS cur;
DROP TABLE IF EXISTS ins;
DROP TABLE IF EXISTS mb;
